import {icons} from "../static/icons_data";
import SimpleCrypto from "simple-crypto-js";
import {SECRETKEY} from "../util/config";

export default function UserInfo(props) {

    const simpleCrypto = new SimpleCrypto(SECRETKEY)
    const cipherText = simpleCrypto.encrypt(props.data.password).toString()

    function getSelectedIcons() {
        const arr = []
        const pattern = props.data.pattern
        for(let i=0; i<pattern.length; i++) {
            for(let j=0; j<icons.length; j++) {
                if (pattern[i] === icons[j].id) {
                    arr.push(<img alt="" src={icons[j].url} key={icons[j].id}/>)
                }
            }
        }
        return arr
    }


    return (
        <div className="font-['Work_Sans'] h-[32rem] mt-12 flex justify-center">
            <div>
                <div className="flex">
                    <p className="text-4xl text-white">{`Username: `}</p>
                    <p className="text-4xl text-green-400 ml-4">{`${props.data.username}`}</p>
                </div>
                <div className="flex">
                    <p className="text-4xl text-white">{`Email: `}</p>
                    <p className="text-4xl text-green-400 ml-4">{`${props.data.email}`}</p>
                </div>
                <div className="flex">
                    <p className="text-4xl text-white">{`Password: `}</p>
                    <p className="text-4xl text-green-400 ml-4 max-w-3xl">{`${cipherText}`}</p>
                </div>
                <p className="text-4xl text-white">{`Selected Images:`}</p>
                <div className="bg-white p-4 rounded-lg mt-4 grid grid-cols-4 justify-items-center">
                    {getSelectedIcons()}
                </div>
            </div>

        </div>
    )
}
