import {faUnlock} from "@fortawesome/free-solid-svg-icons";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";

export default function Home() {

    function handleKnowMore() {
        const element = document.getElementById('home--2')
        if (element) element.scrollIntoView({behavior: "smooth"})
    }

    return (
        <div>
            <div className="flex justify-around mt-16">
                {/*INFO*/}
                <div className="font-['Work_Sans'] text-white ml-8 mt-8">
                    <p className="text-6xl font-bold">Discover </p>
                    <p className="text-6xl font-bold">Graphical Password</p>
                    <p className="text-6xl font-bold">Authentication</p>
                    <p className="text-3xl mt-6">A Novel Approach For Security </p>
                    <p className="text-3xl">And User Experience Of </p>
                    <p className="text-3xl">Graphical Password Authentication.</p>
                    <button onClick={handleKnowMore} className="w-1/3 bg-[#A259FF] rounded-lg px-4 py-1 mt-6 text-xl border-[#A259FF] border-2 hover:bg-transparent">
                        <FontAwesomeIcon className="text-white mr-3" icon={faUnlock} />
                        Know More
                    </button>
                </div>
                {/*IMAGE*/}
                <div className="">
                    <img alt="" className="rounded-[25px] w-4/5" src="https://img.freepik.com/premium-vector/purple-color-cyber-security-concept-with-technology_1302-8584.jpg"/>
                </div>
            </div>

            <div id="home--2" className=" mt-48 h-screen">
                YOO
            </div>
        </div>
    )
}
