import {useState} from "react";
import {icons} from "../static/icons_data";

export default function Signup() {

    const [next, setNext] = useState(false)
    const [iconsData, setIconsData] = useState(icons)
    const [signupInfo, setSignupInfo] = useState({
        username: "",
        email: "",
        password: ""
    })

    function handleChange(event) {
        setSignupInfo(prev => {
            return {
                ...prev,
                [event.target.name]: event.target.value
            }
        })
    }

    function handleNextClick(event) {
        setNext(true)
    }

    return (
        <div className=" h-[32rem] mt-12">
            {!next && <div className="flex justify-center h-full">
                {/*IMAGE*/}
                <div className="">
                    <img className="h-full" alt="" src="../static/img/signup.png"/>
                </div>
                {/*SIGNUP FORM*/}
                <div className="font-['Work_Sans'] mt-4">
                    <p className="text-white text-5xl  font-bold">Create Account</p><br/>
                    <p className="text-white text-2xl">Welcome! Enter Your Details And Experience</p>
                    <p className="text-white text-2xl">Graphical Password System.</p><br/>
                    <div className="flex flex-col w-2/3">
                        <input onChange={handleChange} name="username" className="rounded-full h-12 px-6 font-3xl" type="text" placeholder="Username"/>
                        <input onChange={handleChange} name="email" className="rounded-full h-12 px-6 font-3xl mt-4" type="text" placeholder="Email"/>
                        <input onChange={handleChange} name="password" className="rounded-full h-12 px-6 font-3xl mt-4" type="text" placeholder="Password"/>
                    </div>
                    <button onClick={handleNextClick} className="h-12 bg-[#A259FF] rounded-full px-6 w-2/3 mt-6 text-white border-2 hover:bg-transparent border-[#A259FF] font-bold">Next</button>
                </div>
            </div>}

            {next && <div className="flex justify-center h-full">
                <div className="grid grid-cols-3 bg-white h-full rounded-lg w-1/3 ml-4">

                    <img src="https://img.icons8.com/stickers/100/null/son-goku.png" alt=""/>
                    <img src="https://img.icons8.com/stickers/100/null/son-goku.png" alt=""/>
                    <img src="https://img.icons8.com/stickers/100/null/son-goku.png" alt=""/>
                    <img src="https://img.icons8.com/stickers/100/null/son-goku.png" alt=""/>
                    <img src="https://img.icons8.com/stickers/100/null/son-goku.png" alt=""/>
                    <img src="https://img.icons8.com/stickers/100/null/son-goku.png" alt=""/>
                    <img src="https://img.icons8.com/stickers/100/null/son-goku.png" alt=""/>
                    <img src="https://img.icons8.com/stickers/100/null/son-goku.png" alt=""/>
                    <img src="https://img.icons8.com/stickers/100/null/son-goku.png" alt=""/>

                </div>

                <div className="font-['Work_Sans'] mt-4 ml-16">
                    <p className="text-white text-5xl  font-bold">Set Graphical Password</p><br/>
                    <p className="text-white text-2xl">Select Images For Your Graphical Password.</p>
                    <p className="text-white text-2xl">Chose Minimum 4 Images.</p>
                    <p className="text-white text-2xl">NOTE: Remember The Sequence</p><br/>

                    <button onClick={handleNextClick} className="h-12 bg-[#A259FF] rounded-full px-6 w-2/3 mt-6 text-white border-2 hover:bg-transparent border-[#A259FF] font-bold">Create Account</button>
                </div>
            </div>}


        </div>
    )
}
